rollback.sql:

DECLARE
    l_errors NUMBER;
    l_error_message VARCHAR2(4000);
BEGIN
    EXECUTE IMMEDIATE 'CREATE OR REPLACE PACKAGE BODY MY_PACKAGE AS ...';
    EXCEPTION
        WHEN OTHERS THEN
            l_errors := SQLCODE;
            l_error_message := SQLERRM;

            IF l_errors = -24344 THEN -- ORA-24344: success with compilation error
                EXECUTE IMMEDIATE 'DROP PACKAGE MY_PACKAGE';
                DBMS_OUTPUT.PUT_LINE('Compilation error: ' || l_error_message);
            ELSE
                RAISE;
            END IF;
END;
